INSERT INTO `PREFIX_DB_review` (`review_id`, `product_id`, `customer_id`, `author`, `text`, `rating`, `status`, `date_added`, `date_modified`) VALUES ('23', '41', '0', 'Максим', 'Принципиально выбирал белый корпус. Этот выиграл цена/качество/вид! В начале смотрел на старшую модель (550), но усмотрел проблемы с продуваемостью. NZXT в 2 раза дороже и тоже со своими проблемами. Но если собираетесь ставить &quot;горячую&quot; начинку, то лучше присмотреть более продуваемый корпус или колхозить с перфорацией. Хорошая карта и проц будут быстро создавать в корпусе температуру парилки. 4 вентилятора не успевают прогонять воздух через воздушные сетки на лицевой панеле и верхней крышке. Доставлю ещё 2-3 вентилятора и посмотрю что получится. Отсек для HDD придется варварски демонтировать чтоб не мешал проводам БП.', '5', '1', '2019-07-12 11:12:58', '2020-01-22 11:14:03');
